import Axios from 'axios';
import React, { useEffect, useState } from 'react';
import { domain } from '../../env';
import Product from '../Product/Product';

const HomePage = () => {
    // State to store the products data
    const [products, setProducts] = useState(null);

    // Fetch data when the component mounts
    useEffect(() => {
        const getData = () => {
            Axios({
                method: 'get',
                url: `${domain}/api/product/`,
            }).then((res) => {
                setProducts(res.data);
            });
        };
        getData();
    }, []);

    // Function to fetch the next page of products
    const nextPage = async () => {
        Axios({
            method: 'get',
            url: products?.next,
        }).then((res) => {
            setProducts(res.data);
        });
    };

    // Function to fetch the previous page of products
    const previousPage = async () => {
        Axios({
            method: 'get',
            url: products?.previous,
        }).then((res) => {
            setProducts(res.data);
        });
    };

    return (
        <div className="container-fluid">
            <div className="row ps-md-5 pe-md-5 ps-sm-2 pe-sm-2 mx-auto">
                <div className="row mx-auto">
                    {products !== null ? (
                        <>
                            {products?.results.map((item, i) => (
                                // Display each product using the Product component
                                <div className="col-12 col-sm-8 col-md-6 col-lg-4" key={i}>
                                    <Product item={item} />
                                </div>
                            ))}
                        </>
                    ) : (
                        // Display a loading message while products are being fetched
                        <>
                            <h1>Loading...</h1>
                        </>
                    )}
                </div>
                {/* Pagination controls */}
                <div className="homepage__pagination text-center mt-4">
                    <ul className="pagination">
                        {products?.previous !== null ? (
                            <li className="page-item">
                                <button onClick={previousPage} className="btn btn-link" aria-label="Previous">
                                    <span aria-hidden="true">&laquo; Previous</span>
                                </button>
                            </li>
                        ) : (
                            <li className="page-item disabled">
                                <span className="btn btn-link" aria-disabled="true">&laquo; Previous</span>
                            </li>
                        )}

                        <li className="page-item disabled">
                            <span className="page-link">{products?.number}</span>
                        </li>

                        <li className="page-item disabled">
                            <span className="page-link">of</span>
                        </li>

                        <li className="page-item disabled">
                            <span className="page-link">{products?.num_pages}</span>
                        </li>

                        {products?.next !== null ? (
                            <li className="page-item">
                                <button onClick={nextPage} className="btn btn-link" aria-label="Next">
                                    <span aria-hidden="true">Next &raquo;</span>
                                </button>
                            </li>
                        ) : (
                            <li className="page-item disabled">
                                <span className="btn btn-link" aria-disabled="true">Next &raquo;</span>
                            </li>
                        )}
                    </ul>
                </div>

            </div>
        </div>
    );
};

export default HomePage;
