import Axios from 'axios';
import React, { useEffect, useState } from 'react';
import { domain } from '../../env';
import Product from '../Product/Product';

const HomePage = () => {
    const [products, setProducts] = useState(null);
    const [currentPage, setCurrentPage] = useState(1);
    const [searchQuery, setSearchQuery] = useState('');
    const [error, setError] = useState(null);

    const getData = () => {
        Axios({
            method: 'get',
            url: `${domain}/api/product/?page=${currentPage}&search=${searchQuery}`,
        })
            .then((res) => {
                setProducts(res.data);
            })
            .catch((error) => {
                setError("An error occurred while fetching data. Please try again later.");
                console.error("Error fetching data:", error);
            });
    };

    useEffect(() => {
        getData();
    }, [currentPage, searchQuery]);

    const goToPage = (pageNumber) => {
        setCurrentPage(pageNumber);
    };

    const handleSearch = () => {
        // Reset to the first page when performing a new search
        setCurrentPage(1);
        // Trigger fetching data with the new search query
        getData();
    };

    return (
        <div className="container-fluid">
            <div className="row ps-md-5 pe-md-5 ps-sm-2 pe-sm-2 mx-auto">
                {/* Search bar */}
                <div className="col-12 mb-3">
                    <div className="input-group">
                        <input
                            type="text"
                            className="form-control"
                            placeholder="Search products..."
                            value={searchQuery}
                            onChange={(e) => setSearchQuery(e.target.value)}
                        />
                        <button className="btn btn-outline-secondary" type="button" onClick={handleSearch}>
                            Search
                        </button>
                    </div>
                </div>
                <div className="row mx-auto">
                    {error ? (
                        <div className="col-12">
                            <h1>Error: {error}</h1>
                        </div>
                    ) : (
                        <>
                            {products !== null ? (
                                products.results.map((item, i) => (
                                    <div className="col-12 col-sm-8 col-md-6 col-lg-4" key={i}>
                                        <Product item={item} />
                                    </div>
                                ))
                            ) : (
                                <div className="col-12">
                                    <h1>Loading...</h1>
                                </div>
                            )}
                        </>
                    )}
                </div>
                <div className="homepage__pagination">
                    <div className="pt-3 pb-5 ms-auto">
                        {products?.count &&
                            Array.from({ length: Math.ceil(products.count / 10) }, (_, index) => (
                                <button
                                    key={index + 1}
                                    onClick={() => goToPage(index + 1)}
                                    className={`btn btn-md m-1 ${currentPage === index + 1 ? 'btn-primary' : 'btn-outline-secondary'}`}
                                >
                                    {index + 1}
                                </button>
                            ))}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default HomePage;
