import React from 'react';
import { Link } from 'react-router-dom';
import { useGlobalState } from '../../state/provider';

const NavBar = () => {
    const [{ profile, cart_product_incomplete }, dispatch] = useGlobalState();
    let cart_product_length = 0;
    if (cart_product_incomplete !== null) {
        cart_product_length = cart_product_incomplete[0]?.cart_product.length;
    }

    const logoutButton = () => {
        window.localStorage.clear();
        dispatch({
            type: 'ADD_PROFILE',
            profile: null,
        });
        window.location.href = '/';
    };

    return (
        <>
            <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
                <div className="container">
                    <Link className="navbar-brand" to="/">
                        Ecommerce
                    </Link>
                    <button
                        className="navbar-toggler"
                        type="button"
                        data-toggle="collapse"
                        data-target="#navbarNav"
                        aria-controls="navbarNav"
                        aria-expanded="false"
                        aria-label="Toggle navigation"
                    >
                        <span className="navbar-toggler-icon" />
                    </button>
                    <div className="collapse navbar-collapse" id="navbarNav">
                        <ul className="navbar-nav ml-auto">
                            {profile !== null ? (
                                <>
                                    <li className="nav-item">
                                        <Link to="/cart" className="btn btn-dark">
                                            <i className="fas fa-cart-plus" />
                                            <span>({cart_product_length})</span>
                                        </Link>
                                    </li>
                                    <li className="nav-item">
                                        <Link to="/profile" className="nav-link active">
                                            Profile
                                        </Link>
                                    </li>
                                    <li className="nav-item">
                                        <Link onClick={logoutButton} className="nav-link">
                                            Logout
                                        </Link>
                                    </li>
                                </>
                            ) : (
                                <li className="nav-item">
                                    <Link to="/login" className="nav-link">
                                        Login
                                    </Link>
                                </li>
                            )}
                        </ul>
                    </div>
                </div>
            </nav>
        </>
    );
};

export default NavBar;
