import Axios from 'axios';
import React, { useEffect, useState } from 'react';
import { domain } from '../../env';
import Product from '../Product/Product';

const HomePage = () => {
    const [products, setProducts] = useState(null);

    useEffect(() => {
        const getData = () => {
            Axios({
                method: 'get',
                url: `${domain}/api/product/`,
            }).then((res) => {
                setProducts(res.data);
            });
        };
        getData();
    }, []);

    const nextPage = async () => {
        Axios({
            method: 'get',
            url: products?.next,
        }).then((res) => {
            setProducts(res.data);
        });
    };

    const previousPage = async () => {
        Axios({
            method: 'get',
            url: products?.previous,
        }).then((res) => {
            setProducts(res.data);
        });
    };

    return (
        <div className="container-fluid mt-3">
            <div className="row">
                <div className="col-md-9">
                    <div className="row">
                        {products !== null ? (
                            products?.results.map((item, i) => (
                                <div className="col-12 col-sm-6 col-md-4 mb-4" key={i}>
                                    <Product item={item} />
                                </div>
                            ))
                        ) : (
                            <div className="col-12">
                                <h1>Loading...</h1>
                            </div>
                        )}
                    </div>
                    <div className="homepage__pagination mt-3">
                        <div className="mx-auto">
                            {products?.previous !== null ? (
                                <button onClick={previousPage} className="btn btn-lg btn-success">
                                    <i className="fas fa-backward" /> Previous
                                </button>
                            ) : (
                                <button className="btn btn-lg btn-success" disabled>
                                    {' '}
                                    <i className="fas fa-backward" /> Previous
                                </button>
                            )}
                            {' '}
                            {products?.next !== null ? (
                                <button onClick={nextPage} className="btn btn-lg btn-danger">
                                    Next <i className="fas fa-forward" />
                                </button>
                            ) : (
                                <button className="btn btn-lg btn-danger" disabled>
                                    Next <i className="fas fa-forward" />
                                </button>
                            )}
                        </div>
                    </div>
                </div>
                <div className="col-md-3 mt-3">
                    {/* Sidebar or additional content can go here */}
                </div>
            </div>
        </div>
    );
};

export default HomePage;
